import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int res = 1;
        for (int i = 0; i < 5; i++) {
            res *= (n - i);
        }
        System.out.println(res);
    }
