const MOD = 1e9 + 7;
const s = require('fs').readFileSync(0, 'utf-8').trim();
const n = s.length;
const pow10 = Array(n + 1).fill(1);
for (let i = 1; i <= n; ++i)
    pow10[i] = (pow10[i - 1] * 10) % MOD;

const prefix = Array(n + 1).fill(0);
for (let i = 0; i < n; ++i)
    prefix[i + 1] = (prefix[i] * 10 + Number(s[i])) % MOD;

let total = 0;
for (let i = 0; i < n; ++i) {
    for (let j = i + 1; j <= n; ++j) {
        const left = prefix[i];
        let right = (prefix[n] - prefix[j] * pow10[n - j]) % MOD;
        if (right < 0) right += MOD;
        const val = (left * pow10[n - j] + right) % MOD;
        total = (total + val) % MOD;
    }
}
