import java.util.*;
public class Main {
    static final int MOD = 1000000007;
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        int n = s.length();
        long[] pow10 = new long[n + 1];
        pow10[0] = 1;
        for (int i = 1; i <= n; i++)
            pow10[i] = (pow10[i - 1] * 10) % MOD;
        long[] prefix = new long[n + 1];
        for (int i = 0; i < n; i++)
            prefix[i + 1] = (prefix[i] * 10 + (s.charAt(i) - '0')) % MOD;
        long total = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j <= n; j++) {
                long left = prefix[i];
                long right = (prefix[n] - prefix[j] * pow10[n - j]) % MOD;
                if (right < 0) right += MOD;
                long val = (left * pow10[n - j] + right) % MOD;
                total = (total + val) % MOD;
            }
        }
        System.out.println(total);
    }
}
