#include <iostream>
#include <string>
using namespace std;

int main() {
    int n;
    string s;
    cin >> n >> s;
    long long res = 0;
    for (int i = 0; i < n; ++i) {
        int max_len = 0, count = 0;
        for (int j = i; j < n; ++j) {
            if (s[j] == '1') count++;
            else count = 0;
            max_len = max(max_len, count);
            res += max_len;
        }
    }
    cout << res << endl;
    return 0;
}
