import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String s = sc.next();
        long res = 0;
        for (int i = 0; i < n; i++) {
            int count = 0, maxLen = 0;
            for (int j = i; j < n; j++) {
                if (s.charAt(j) == '1') count++;
                else count = 0;
                maxLen = Math.max(maxLen, count);
                res += maxLen;
            }
        }
        System.out.println(res);
    }
}
