#include <iostream>
#include <vector>
#include <cmath>
using namespace std;

int main() {
    int t;
    cin >> t;
    while (t--) {
        int n;
        cin >> n;
        vector<int> res(n, -1);
        int i = n - 1;
        while (i >= 0) {
            int sq = sqrt(2 * i);
            while (sq * sq > 2 * i) sq--;
            int j = sq * sq - i;
            if (j < 0 || j > i) {
                cout << -1 << endl;
                goto next;
            }
            for (int k = j; k <= i; ++k)
                res[k] = j + i - k;
            i = j - 1;
        }
        for (int x : res) cout << x << " ";
        cout << endl;
    next:;
    }
    return 0;
}
