const readline = require('readline');
const rl = readline.createInterface({ input: process.stdin, output: process.stdout });
let input = [];
rl.on('line', line => input.push(line));
rl.on('close', () => {
    let t = parseInt(input[0]);
    let idx = 1;
    while (t--) {
        let n = parseInt(input[idx++]);
        let res = Array(n).fill(-1);
        let i = n - 1;
        let failed = false;
        while (i >= 0) {
            let sq = Math.floor(Math.sqrt(2 * i));
            while (sq * sq > 2 * i) sq--;
            let j = sq * sq - i;
            if (j < 0 || j > i) {
                console.log(-1);
                failed = true;
                break;
            }
            for (let k = j; k <= i; ++k) {
                res[k] = j + i - k;
            }
            i = j - 1;
        }
        if (!failed) console.log(res.join(' '));
    }
});
