import java.util.*;
public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int t = sc.nextInt();
        while (t-- > 0) {
            int n = sc.nextInt();
            int[] res = new int[n];
            Arrays.fill(res, -1);
            int i = n - 1;
            boolean ok = true;
            while (i >= 0) {
                int sq = (int) Math.sqrt(2 * i);
                while (sq * sq > 2 * i) sq--;
                int j = sq * sq - i;
                if (j < 0 || j > i) {
                    System.out.println(-1);
                    ok = false;
                    break;
                }
                for (int k = j; k <= i; k++)
                    res[k] = j + i - k;
                i = j - 1;
            }
            if (ok) {
                for (int k = 0; k < n; k++)
                    System.out.print(res[k] + " ");
                System.out.println();
            }
        }
    }
}
