#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;

int process(vector<int>& a, vector<int>& b) {
    int res = 0;
    int j = 0;
    for (int i = 0; i < b.size(); ++i) {
        while (j < a.size() && a[j] <= b[i]) ++j;
        res = max(res, j);
    }
    return res;
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        int n, m;
        cin >> n >> m;
        vector<int> a(n), b(m);
        for (int i = 0; i < n; ++i) cin >> a[i];
        for (int i = 0; i < m; ++i) cin >> b[i];

        vector<int> a_pos, b_pos, a_neg, b_neg;
        for (int x : a) {
            if (x > 0) a_pos.push_back(x);
            else a_neg.push_back(-x);
        }
        for (int x : b) {
            if (x > 0) b_pos.push_back(x);
            else b_neg.push_back(-x);
        }
        reverse(a_neg.begin(), a_neg.end());
        reverse(b_neg.begin(), b_neg.end());

        int ans = process(a_pos, b_pos) + process(a_neg, b_neg);
        cout << ans << endl;
    }
    return 0;
}
