function process(a, b) {
    let res = 0;
    let j = 0;
    for (let i = 0; i < b.length; i++) {
        while (j < a.length && a[j] <= b[i]) j++;
        res = Math.max(res, j);
    }
    return res;
}

const readline = require('readline');
const rl = readline.createInterface({ input: process.stdin });

let input = [];
rl.on('line', line => input.push(line));
rl.on('close', () => {
    let t = parseInt(input[0]);
    let idx = 1;
    for (let test = 0; test < t; test++) {
        let [n, m] = input[idx++].split(' ').map(Number);
        let a = input[idx++].split(' ').map(Number);
        let b = input[idx++].split(' ').map(Number);

        let a_pos = a.filter(x => x > 0);
        let b_pos = b.filter(x => x > 0);
        let a_neg = a.filter(x => x < 0).map(x => -x).reverse();
        let b_neg = b.filter(x => x < 0).map(x => -x).reverse();

        let ans = process(a_pos, b_pos) + process(a_neg, b_neg);
        console.log(ans);
    }
});
