import java.util.*;

public class Main {
    static int process(List<Integer> a, List<Integer> b) {
        int res = 0, j = 0;
        for (int i = 0; i < b.size(); i++) {
            while (j < a.size() && a.get(j) <= b.get(i)) j++;
            res = Math.max(res, j);
        }
        return res;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int t = sc.nextInt();
        while (t-- > 0) {
            int n = sc.nextInt(), m = sc.nextInt();
            List<Integer> a = new ArrayList<>(), b = new ArrayList<>();
            for (int i = 0; i < n; i++) a.add(sc.nextInt());
            for (int i = 0; i < m; i++) b.add(sc.nextInt());

            List<Integer> a_pos = new ArrayList<>(), b_pos = new ArrayList<>(), a_neg = new ArrayList<>(), b_neg = new ArrayList<>();
            for (int x : a) {
                if (x > 0) a_pos.add(x);
                else a_neg.add(-x);
            }
            for (int x : b) {
                if (x > 0) b_pos.add(x);
                else b_neg.add(-x);
            }
            Collections.reverse(a_neg);
            Collections.reverse(b_neg);

            int ans = process(a_pos, b_pos) + process(a_neg, b_neg);
            System.out.println(ans);
        }
        sc.close();
    }
}
